mapper.select:
select count(1)
from tb_item

  where status = #{state}

select

from tb_item


  (title like #{searchKey}
  or
  sell_point like #{searchKey}
  or
  price like #{searchKey}
  or
  id like #{searchKey})


    and cid = #{cid}



    and
    created between #{minDate} and #{maxDate}




  order by
  ${orderCol}
  ${orderDir}
