mapper.select:
select *
from tb_member
where
  state != 2

    and
    (username like #{searchKey}
    or
    sex like #{searchKey}
    or
    phone like #{searchKey}
    or
    email like #{searchKey}
    or
    address like #{searchKey})


  and
  created between #{minDate} and #{maxDate}


  order by ${orderCol} ${orderDir}

select *
from tb_member
where
state = 2

  and
  (username like #{searchKey}
  or
  sex like #{searchKey}
  or
  phone like #{searchKey}
  or
  email like #{searchKey}
  or
  address like #{searchKey})


  and
  created BETWEEN #{minDate} AND #{maxDate}


  order by ${orderCol} ${orderDir}

select count(1) from tb_member
where
  username = #{username}

select count(1) from tb_member
where
  username = #{username}
  and
  id != #{id}

select count(1) from tb_member where
  phone = #{phone}
  and
  id != #{id}

select count(1) from tb_member where
  email = #{email}
  and
  id != #{id}
